<?phpclass Content {    public function __construct($path) {        $this->path = $path;    }    public function get() {        return file_get_contents($this->path);    }    public function put($content) {        file_put_contents($this->path, $content);    }    public function append($content) {        file_put_contents($this->path, $content, FILE_APPEND);    }    public function prepend($content) {        $this->put($content . $this->get());    }}
